//Java program to check leap year

package practice;

public class LeapYear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year = 1995;
		
		if(year % 4 == 0) {
			System.out.println("1995 is the leap year");
		}
		else {
			System.out.println("1995 is not the leap year");
		}
	}

}
